Scripts.OnTick:
local coef;
if (unit:IsInCombat()) then
    coef = unit:GetInCombatHealthRegenRatio();
else
    coef = unit:GetHealthRegenRatio();
end
if (unit:IsSitting()) then
    coef = coef*1.33;
end

unit:AddHealth(math.ceil(coef*(unit:GetSpirit()*unit:GetSpiritHealthRegenRatio() + 6)));
