Scripts.OnLoad:
this.normalTextColor = Color(1,1,1);
this:RegisterEvent("ADDON_LOADED");
AddOns.Editor:RegisterTextColorFunction(this, function (self, color)
    local newColor = self.normalTextColor*color;
    self:GetNormalFontObject():SetTextColor(newColor:Unpack());
end);

Scripts.OnClick:
if (UIParent.activeDropdown) then
    if (UIParent.activeDropdown ~= this.Dropdown) then
        UIParent.activeDropdown:Hide();
        UIParent.activeDropdown = this.Dropdown;
        this.Dropdown:Show();
    else
        UIParent.activeDropdown = nil;
        this.Dropdown:Hide();
    end
else
    this.Dropdown:Show();
    UIParent.activeDropdown = this.Dropdown;
end

Scripts.OnLoad:
this.normalTextColor = Color(1, 1, 1);

this:RegisterEvent("ADDON_LOADED");
AddOns.Editor:RegisterTextColorFunction(this, function (self, color)
    local newColor = self.normalTextColor*color;
    self:GetNormalFontObject():SetTextColor(newColor:Unpack());
end);
AddOns.Editor:RegisterSecondaryTextColorFunction(this, function (self, color)
    if (self.Key) then
        self.Key:SetTextColor(color:Unpack());
    end
end);

Scripts.OnEvent:
if (event == "ADDON_LOADED") then
    local newColor = this.normalTextColor*AddOns.Editor.Config.UITextColor;
    this:GetNormalFontObject():SetTextColor(newColor:Unpack());
    if (this.Key) then
        this.Key:SetTextColor(AddOns.Editor.Config.UISecondaryTextColor:Unpack());
    end
end
