Scripts.OnLoad:
                this.normalTextColor  = Color(1, 1, 1);
                this.normalBgColor    = Color(0.75, 0.75, 0.75);
                this.normalTitleColor = Color(0.5, 0.5, 0.5);

                this:RegisterEvent("ADDON_LOADED");
                AddOns.Editor:RegisterBackgroundColorFunction(this, function (self, color)
                    local newColor = self.normalBgColor*color;
                    self:SetBackdropColor(newColor:Unpack());

                    newColor = self.normalTitleColor*color;
                    newColor.a = self.normalTitleColor.a;
                    self.Title:SetBackdropColor(newColor:Unpack());
                end);
                AddOns.Editor:RegisterTextColorFunction(this, function (self, color)
                    local newColor = self.normalTextColor*color;
                    self.Title.TitleString:SetTextColor(newColor:Unpack());
                    self.Title.CloseButton:GetNormalFontObject():SetTextColor(newColor:Unpack());
                    self.Content:SetTextColor(newColor:Unpack());
                end);

                this.Title.TitleString:SetText(AddOns.Templates:GetLocalizedString("ErrorDialogTitle"));

Scripts.OnEvent:
                if (event == "ADDON_LOADED") then
                    local newColor = this.normalBgColor*AddOns.Editor.Config.UIBackgroundColor;
                    this:SetBackdropColor(newColor:Unpack());

                    newColor = this.normalTitleColor*AddOns.Editor.Config.UIBackgroundColor;
                    newColor.a = this.normalTitleColor.a;
                    this.Title:SetBackdrop({});
                    this.Title:SetBackdropColor(newColor:Unpack());

                    newColor = this.normalTextColor*AddOns.Editor.Config.UITextColor;
                    this.Title.TitleString:SetTextColor(newColor:Unpack());
                    this.Title.CloseButton:GetNormalFontObject():SetTextColor(newColor:Unpack());
                    this.Content:SetTextColor(newColor:Unpack());
                end

Scripts.OnClick:
this:GetParent():Hide();
if (this:GetParent().OnOK ~= nil) then
    this:GetParent():OnOK();
end
